mapper.select:
select
*
from crm_order

    1 = 1

         and id = #{obj.id}


         and order_details_no = #{obj.orderDetailsNo}


         and order_no = #{obj.orderNo}


         and org_id = #{obj.orgId}


         and org_name = #{obj.orgName}


         and organization_id = #{obj.organizationId}


         and organization_name = #{obj.organizationName}


         and sub_id = #{obj.subId}


         and sub_name = #{obj.subName}


         and class_id = #{obj.classId}


         and clazz_name = #{obj.clazzName}


         and class_schedule_name = #{obj.classScheduleName}


         and class_schedule_id = #{obj.classScheduleId}


         and user_id = #{obj.userId}


         and teacher_id = #{obj.teacherId}


         and teacher_name = #{obj.teacherName}


         and clazz_type = #{obj.clazzType}


         and student_id = #{obj.studentId}


         and student_name = #{obj.studentName}


         and classroom_id = #{obj.classroomId}


         and classroom_name = #{obj.classroomName}


         and consultant_id = #{obj.consultantId}


         and good_price = #{obj.goodPrice}


         and clubcard_id = #{obj.clubcardId}


         and clubcard_money = #{obj.clubcardMoney}


         and clubcard_discounts_money = #{obj.clubcardDiscountsMoney}


         and coupons_id = #{obj.couponsId}


         and coupons_money = #{obj.couponsMoney}


         and coupons_discounts_money = #{obj.couponsDiscountsMoney}


         and sum_money = #{obj.sumMoney}


         and discounts_money = #{obj.discountsMoney}


         and order_status = #{obj.orderStatus}


         and pay_time = #{obj.payTime}


         and ctime = #{obj.ctime}


         and utime = #{obj.utime}


         and created_by = #{obj.createdBy}


         and last_updated_by = #{obj.lastUpdatedBy}


         and version_num = #{obj.versionNum}


         and source = #{obj.source}


         and way = #{obj.way}
