mapper.select:
select
*
from crm_sales_opportunity

    1 = 1

         and id = #{obj.id}


         and customer_id = #{obj.customerId}


         and contact_id = #{obj.contactId}


         and product_id = #{obj.productId}


         and sales_amount = #{obj.salesAmount}


         and sales_date = #{obj.salesDate}


         and status = #{obj.status}


         and details_status_id = #{obj.detailsStatusId}


         and sales_lead_id = #{obj.salesLeadId}


         and principal_id = #{obj.principalId}


         and organization_id = #{obj.organizationId}


         and transfer_date = #{obj.transferDate}


         and is_star = #{obj.isStar}


         and is_view = #{obj.isView}


         and is_deleted = #{obj.isDeleted}


         and version_num = #{obj.versionNum}


         and ctime = #{obj.ctime}


         and created_by = #{obj.createdBy}


         and utime = #{obj.utime}


         and last_updated_by = #{obj.lastUpdatedBy}


         and opportunity_property = #{obj.opportunityProperty}


         and amount = #{obj.amount}


         and pay_type = #{obj.payType}


         and remark = #{obj.remark}
